    if (err)
      throw err
    response.redirect('/admin/getNews')
  })
})

router.route('/deleteNews').post(json(),urlencoded({extended:false}), cors(corsOptions), (request, response) => {
  const id = request.body.id
  news.findByIdAndDelete(id, (err, result) => {
    if (err)
      throw err
    response.redirect('/admin/getNews')
  })
})

